import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, SafeAreaView, Image, ScrollView, View, Dimensions, TouchableOpacity, Platform } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import * as ImagePicker from 'expo-image-picker';
import {foodName} from '../assets/foodName.json'

export default function uploadImg({ navigation }) {
  const b64ImagePlaceholder = "data:image/jpeg;base64,";
  const [b64Image, setB64Image] = useState(b64ImagePlaceholder);
  const [foodInImg, setFoodInImg] = useState([]);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        }
      }
    })();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      base64: true,
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      quality: 1,
    });

    if (!result.cancelled) {
      setB64Image(result.uri.substring(0, 4) === "data" ? result.uri : `data:image/jpeg;base64,${result.base64}`);
    }
  };


  const getCleanB64String = (b64string) => {
    // We want to remove the first chunk (e.g. "data:image/jpeg;base64,")
    // so we find the comma and return the string after it
    const idx = b64string.indexOf(',');
    return b64string.substring(idx + 1) 
  }

  function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  const getLabels = async () =>{
    console.log("pressed")
    const content = getCleanB64String(b64Image);
    let data =JSON.stringify({"requests":[{"image":{"content":content},"features":[{"type":"LABEL_DETECTION"}]}]});

    let  myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: data,
      redirect: 'follow'
    };

    fetch(`https://vision.googleapis.com/v1/images:annotate?key=${process.env.API_KEY}`, requestOptions)
      .then(response => response.text())
      .then(result => {
        console.log(result)
        const response = JSON.parse(result)["responses"][0]['labelAnnotations'];
          response.forEach(item => {
            let name = item.description.toLowerCase();

            // brute force search
            foodName.forEach( fn => {
              if (fn.includes(name)){
                console.log(fn, name)
                foodInImg.push(name);
              }
            })

            sleep(1000).then(() => {
                // redirect
                navigation.navigate('Result', {
                  b64Img: b64Image,
                  foodInImg: foodInImg
               });
            })
          })
      })
      .catch(error => console.log('error', error));
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        <View style={styles.view}>
        <LinearGradient
        // Background Linear Gradient
        colors={['rgba(171,101,215,255)', 'rgba(210,138,181,255)']}
        style={styles.background}
      />
        <Text style={[styles.text, styles.title]}>Waste Fighter</Text>
        <Image source ={require('../assets/logo.png')} />
        <Text style={[styles.text, styles.paragraph]}>Please capture an image of your leftover food:</Text>
        <TouchableOpacity style={styles.button}
          onPress={() => pickImage()}>
          {<Image source={{ uri: b64Image }} style={{width:150, height:150}} />}
        </TouchableOpacity>
        
        <Text style={[styles.text, styles.paragraph]}>You will be shown the nearest foodbanks based on your location and our distribution system.</Text>
        <TouchableOpacity style={[styles.button, {width: '90%'}]}
          onPress={() => getLabels()}>
          <Text style={{textTransform: 'uppercase', fontWeight: '600'}}>Next</Text>
        </TouchableOpacity>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex:1,
  },
  view: {
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingBottom: 40,
  },
  background: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    minHeight: Dimensions.get('window').height,
  },
  text: {
    marginHorizontal: 20,
    marginVertical: 10,
    color: '#ffeda4',
  },
  title: {
    marginTop: 40,
    fontSize: 35,
    textShadowColor: '#e7890d',
    textShadowOffset: {width: -1, height: 1},
    textShadowRadius: 5,
    textTransform: 'uppercase'
  },
  paragraph: {
    fontSize: 20
  },
  button:{
    alignItems: 'center',
    justifyContent: 'flex-start',
    padding: 20,
    borderColor: 2,
    backgroundColor: 'white',
    borderRadius: 5,
    shadowColor: 'black',
    shadowOpacity: 0.3,
    shadowOffset : { width: 5, height: 5},
    marginVertical: 20,
  }
});
